		CASE WHEN EDAD_ANOS =5 THEN 
					CASE WHEN [NO_CUMPLE_EDUCACION]= 1 THEN 1 ELSE 0 END
					ELSE NULL END AS NO_CUMPLE_EDUCACION_5  ,
		CASE WHEN EDAD_ANOS >5 AND EDAD_ANOS <=7  THEN  
					CASE WHEN [NO_CUMPLE_EDUCACION]= 1 THEN 1 ELSE 0 END
					ELSE NULL END AS NO_CUMPLE_EDUCACION_MAS_5  								
	from [RYR_COMUNITARIO].[V_RYR_PERSONAS_COMUNIDAD] AS P
	WHERE P.ID_PERSONA NOT IN( SELECT ID_PERSONA FROM [RYR_COMUNITARIO].TB_PLAN_ACCION_TRASLADO_PERSONA_NO_TRASLADA AS NT WHERE  P.ID_PERSONA =NT.ID_PERSONA AND NT.ELIMINADO = 0)	
